use std::hash::{Hash, Hasher};

use super::super::inputs::{Input, Position};

/// An `enum` representing tokens generated by a parser.
#[derive(Debug, Eq)]
pub enum Token<Rule, I: Input> {
    /// The starting bit of the `Token`
    Start {
        rule: Rule,
        pos:  Position<I>
    },
    /// The ending bit of the `Token`
    End {
        rule: Rule,
        pos:  Position<I>
    }
}

impl<Rule: Copy, I: Input> Clone for Token<Rule, I> {
    fn clone(&self) -> Token<Rule, I> {
        match *self {
            Token::Start { ref rule, ref pos } => {
                Token::Start {
                    rule: *rule,
                    pos:  pos.clone()
                }
            },
            Token::End { ref rule, ref pos } => {
                Token::End {
                    rule: *rule,
                    pos:  pos.clone()
                }
            }
        }
    }
}

impl<Rule: PartialEq, I: Input> PartialEq for Token<Rule, I> {
    fn eq(&self, other: &Token<Rule, I>) -> bool {
        match *self {
            Token::Start { ref rule, ref pos } => {
                match *other {
                    Token::Start { rule: ref other_rule, pos: ref other_pos } => {
                        rule == other_rule && pos == other_pos
                    },
                    _ => false
                }
            },
            Token::End { ref rule, ref pos } => {
                match *other {
                    Token::End { rule: ref other_rule, pos: ref other_pos } => {
                        rule == other_rule && pos == other_pos
                    },
                    _ => false
                }
            }
        }
    }
}

impl<Rule: Hash, I: Input> Hash for Token<Rule, I> {
    fn hash<H: Hasher>(&self, state: &mut H) {
        match *self {
            Token::Start { ref rule, ref pos } => {
                rule.hash(state);
                pos.hash(state);
            },
            Token::End { ref rule, ref pos } => {
                rule.hash(state);
                pos.hash(state);
            }
        }
    }
}
